SELECT NVL( SUBSTR(NEW_DPL_ID, 1, 11) || LPAD( (TO_NUMBER(SUBSTR(NEW_DPL_ID, 12, 5)) + 1) , 5, '0') , 'DPL' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '00001' ) AS NEW_DPL_ID FROM ( SELECT MAX(DPL_ID) AS NEW_DPL_ID FROM DPL1000 A WHERE 1=1 AND A.PRJ_ID = #prjId# AND A.DPL_ID LIKE 'DPL' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '%' )
INSERT INTO DPL1000 ( PRJ_ID, DPL_ID, DPL_NM, DPL_VER, DPL_DT , DPL_USR_ID, DPL_DESC, DPL_STS_CD, DPL_TYPE_CD, DPL_AUTO_AFTER_CD , DPL_AUTO_DTM, DPL_RESTORE_CD, DPL_SIGN_TXT, DPL_DEL_CD, DPL_REVISION_NUM , DPL_SIGN_USE_CD, REG_DTM, REG_USR_ID, REG_USR_IP , MODIFY_DTM, MODIFY_USR_ID, MODIFY_USR_IP
) VALUES ( #prjId#, #newDplId#, #dplNm#, #dplVer#, REPLACE(#dplDt#, '-' ,'') , #dplUsrId#, #dplDesc#, #dplStsCd#, #dplTypeCd#, #dplAutoAfterCd# , #dplAutoDtm#, #dplRestoreCd#, #dplSignTxt#, '02', #dplRevisionNum# , #dplSignUseCd#, SYSDATE, #regUsrId#, #regUsrIp# , SYSDATE, #regUsrId#, #regUsrIp# )

SELECT NVL(MAX(BLD_SEQ)+1,1) AS NEW_BLD_SEQ FROM DPL1400 A WHERE 1=1 AND A.PRJ_ID = #prjId# AND A.DPL_ID = #dplId# AND A.JEN_ID = #jenId# AND A.JOB_ID = #jobId#
INSERT INTO DPL1400 ( PRJ_ID ,DPL_ID ,JEN_ID ,JOB_ID ,BLD_SEQ ,BLD_NUM ,BLD_MAIN_NUM ,BLD_RESULT ,BLD_DURATION_TM ,BLD_START_DTM ,BLD_CONSOLE_LOG ,BLD_RESULT_MSG ,REG_DTM ,REG_USR_ID ,REG_USR_IP ,BLD_RESULT_CD
) VALUES ( #prjId# ,#dplId# ,#jenId# ,#jobId# ,#bldSeq# ,#bldNum# ,#bldMainNum# ,#bldResult# ,#estimatedDuration# ,#bldStartDtm# ,#bldConsoleLog# ,#bldResultMsg# ,SYSDATE ,#regUsrId# ,#regUsrIp# ,#bldResultCd# )
